mapper.update:
UPDATE tbl_users set mobile = #{mobile},user_name = #{userName} , cardNo = #{cardNo}
      ,bankNo = #{bankNo}, bankCardNo = #{bankCardNo},role_id = #{role_id}
  where user_id = #{id}

mapper.select:
select count(a.user_name) as total from tbl_users a left join tbl_roots b on a.user_id = b.userid

    where b.companyNumber in

        #{companyNumber}

select d.bankName ,e.companyNumber,a.role_id,a.mobile,a.cardNo, e.dept_number,a.user_id as id,a.user_name as userName,a.bankNo,a.bankCardNo, b.name as com_name,c.name as dept_name,e.groupNumber , f.role_name from tbl_users a left join tbl_banks d on a.bankNo = d.bankNo left join tbl_roots e on a.user_id = e.userid LEFT JOIN tbl_company b on e.companyNumber = b.companyNumber left join tbl_departments c on e.dept_number = c.dept_number left join tbl_roles f on a.role_id = f.role_id where 1=1
and a.user_name like concat('%',#{search},'%') or a.cardNo like concat('%',#{search},'%')
and e.companyNumber in
#{companyNumber}
limit #{pageNum},#{pageSize}
