mapper.update:
UPDATE tbl_users set mobile = #{mobile},user_name = #{userName} , cardNo = #{cardNo} ,
companyNumber = #{companyNumber},dept_number = #{dept_number},bankNo = #{bankNo}, bankCardNo = #{bankCardNo}
  where user_name = #{userName}

mapper.select:
select b.name as com_name,c.name as dept_name,d.bankName as bank,a.companyNumber, a.dept_number,a.user_id as id,a.user_name as userName,a.bankNo,a.bankCardNo,a.mobile,a.cardNo from tbl_users a LEFT JOIN tbl_company b on a.companyNumber = b.companyNumber left join tbl_departments c on a.dept_number = c.dept_number left join tbl_banks d on a.bankNo = d.bankNo
where a.user_name like concat('%',#{search},'%') or a.cardNo like concat('%',#{search},'%')
limit #{pageNum},#{pageSize}
