mapper.select:
select a.id,a.leaveType,a.startDate,a.endDate,a.leaveTime,a.auditor,a.reason,a.createDate,a.currentAuditor, a.status,a.disable,a.type,b.holiday,b.mode,c.user_name as username ,c.image from tbl_leave_record a left join tbl_overtime_details b on a.id = b.recordNo left join tbl_users c on a.userid = c.user_id where a.id in
#{ids}
order by a.editDate desc

select a.user_id as id,a.user_name as userName,a.image,a.disabled,c.name as dept_name,a.createTime  from tbl_users a
left join tbl_roots b on a.user_id = b.userid
left join tbl_departments c on b.dept_number = c.dept_number
where 1=1

    and  a.createTime > #{date}

order by a.createTime limit #{pageNum},#{pageSize}
