mapper.select:
SELECT  a.role_id,a.role_name,a.companyNumber FROM tbl_roles a

    where  a.companyNumber in

        #{companyNumber}

SELECT DISTINCT a.role_id,a.role_name,a.companyNumber,a.companyName FROM tbl_roles a left join tbl_users b on a.role_id = b.role_id left join (select userid,groupNumber from tbl_roots group by userid,groupNumber) c on b.user_id = c.userid where 1=1
and a.role_name like concat('%',#{search},'%')
and a.companyNumber in
#{companyNumber}
ORDER BY role_id limit #{pageNum},#{pageSize}
